import { createBoard } from '@wixc3/react-board';
import { useState } from 'react';
import Alert from 'react-bootstrap/Alert';
// board cover
import cover from './danger-dismissing-alert.svg';

export default createBoard({
    name: 'Danger Dismissing Alert',
    Board: () => {
        const [show, setShow] = useState(true);

        function doSomething() {
            setShow(false);
        }

        return (
            <Alert
                variant="danger"
                show={show}
                onClose={() => doSomething()}
                dismissible
            >
                <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
                <p>
                    Change this and that and try again. Duis mollis, est non
                    commodo luctus, nisi erat porttitor ligula, eget lacinia
                    odio sem nec elit. Cras mattis consectetur purus sit amet
                    fermentum.
                </p>
            </Alert>
        );
    },
    tags: ['alert', 'danger', 'dismissing'],
    cover,
});
