import { createBoard } from '@wixc3/react-board';
import { useState } from 'react';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
// board cover
import cover from './primary-radio-toggle-button-group.svg';

export default createBoard({
    name: 'Primary Toggle Button Group',
    Board: () => {
        const [radioValue, setRadioValue] = useState('1');

        const radios = [
            { name: 'Active', value: '1' },
            { name: 'Radio', value: '2' },
            { name: 'Radio', value: '3' },
        ];

        return (
            <ButtonGroup>
                {radios.map((radio, idx) => (
                    <ToggleButton
                        key={idx}
                        id={`radio-${idx}`}
                        type="radio"
                        variant="primary"
                        name="radio"
                        value={radio.value}
                        checked={radioValue === radio.value}
                        onChange={(e) => setRadioValue(e.currentTarget.value)}
                    >
                        {radio.name}
                    </ToggleButton>
                ))}
            </ButtonGroup>
        );
    },
    tags: ['button group', 'primary', 'toggle button', 'radio'],
    cover,
});
